import { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

export function UpdateTodo({ handleEdited, handleClose }) {
  const { id } = useParams(); // Obtener el id desde los parámetros de la URL
  const [data, setData] = useState({
    title: "",
    description: "",
    date: "",
    priority: "Low",
    done: false,
  });

  // Cargar los datos existentes de la tarea cuando se abre el formulario
  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}todo/${id}`)
      .then((res) => {
        setData(res.data); // Establecer los datos de la tarea en el estado
      })
      .catch((err) => {
        console.error("Error al cargar los datos de la tarea:", err.message);
      });
  }, [id]);

  // Manejar los cambios en el formulario
  function handleChange(e) {
    const { name, value, type, checked } = e.target;
    setData((prevData) => ({
      ...prevData,
      [name]: type === "checkbox" ? checked : value,
    }));
  }

  // Enviar los datos actualizados de la tarea al servidor
  function handleSubmit(e) {
    e.preventDefault();

    axios
      .put(`${process.env.REACT_APP_API_URL}todo/${id}`, data)
      .then((res) => {
        console.log(res.data.message);
        handleEdited(); // Llamar a la función para indicar que la tarea fue editada
        handleClose(); // Cerrar el modal o la vista de actualización
      })
      .catch((err) => {
        console.error("Error al actualizar la tarea:", err.message);
      });
  }

  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <label htmlFor="title" className="label">
        Título
      </label>
      <input
        type="text"
        name="title"
        className="input"
        value={data.title}
        onChange={handleChange}
      />

      <label htmlFor="description" className="label">
        Descripción
      </label>
      <input
        type="text"
        name="description"
        className="input"
        value={data.description}
        onChange={handleChange}
      />

      <label htmlFor="date" className="label">
        Fecha
      </label>
      <input
        type="date"
        name="date"
        className="input"
        value={data.date}
        onChange={handleChange}
      />

      <label htmlFor="priority" className="label">
        Prioridad
      </label>
      <select
        name="priority"
        className="input"
        value={data.priority}
        onChange={handleChange}
      >
        <option value="Low">Baja</option>
        <option value="Medium">Media</option>
        <option value="High">Alta</option>
      </select>

      <div className="checkbox-container">
        <label htmlFor="done" className="label">
          Hecho
        </label>
        <input
          type="checkbox"
          name="done"
          className="checkbox"
          checked={data.done}
          onChange={handleChange}
        />
      </div>

      <button type="submit" className="button">
        Actualizar tarea
      </button>
    </form>
  );
}

export default UpdateTodo;
