import { useState, useEffect } from "react";
import axios from "axios";
import UpdateTodo from "./components/UpdateTodo"; 

export function App() {
    const [todos, setTodos] = useState([]);
    const [selectedTodo, setSelectedTodo] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        // Cargar las tareas desde la API
        axios
            .get(`${process.env.REACT_APP_API_URL}todo`)
            .then((res) => {
                setTodos(res.data);
            })
            .catch((err) => {
                console.error("Error al cargar las tareas:", err.message);
            });
    }, []);

    const handleEdited = (id, updatedTask) => {
        // Actualiza la tarea en el estado
        setTodos((prevTodos) =>
            prevTodos.map((todo) =>
                todo._id === id ? { ...todo, ...updatedTask } : todo
            )
        );
    };

    const handleEdit = (id) => {
        // Abre el modal para editar la tarea
        setSelectedTodo(id);
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        // Cierra el modal
        setIsModalOpen(false);
        setSelectedTodo(null);
    };

    return (
        <div>
            <h1>Lista de Tareas</h1>
            <ul>
                {todos.map((todo) => (
                    <li key={todo._id}>
                        {todo.title} - {todo.description}
                        <button onClick={() => handleEdit(todo._id)}>
                            Editar
                        </button>
                    </li>
                ))}
            </ul>

            {isModalOpen && selectedTodo && (
                <UpdateTodo
                    id={selectedTodo}
                    handleClose={handleCloseModal}
                    handleEdited={handleEdited}
                />
            )}
        </div>
    );
}

export default App;
