import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { useState } from "react"; // Asegúrate de importar useState
import ShowTodoList from './components/showTodoList'; 
import { CreateTodo } from './components/createTodo'; 
import UpdateTodo from './components/updateTodo'; 
import './App.scss'; 

function App() {
  const [todos, setTodos] = useState([]); // Este es el hook de estado

  // Función para manejar la adición de tareas 
  const handleAddTodo = (newTodo) => {
    // Actualiza el estado para incluir la nueva tarea
    setTodos((prevTodos) => [...prevTodos, newTodo]);
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<ShowTodoList todos={todos} />} />
        <Route path="/create" element={<CreateTodo handleAddTodo={handleAddTodo} />} />
        <Route path="/update/:id" element={<UpdateTodo />} />
      </Routes>
    </Router>
  );
}

export default App;
