import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import ShowTodoList from "./components/showTodoList";
import CreateTodo from "./components/createTodo";
import UpdateTodo from "./components/updateTodo";
import "./App.scss";

function App() {
  const [todos, setTodos] = useState([]);

  // Función para cargar las tareas desde el servidor
  const fetchTodos = () => {
    axios
      .get(`${process.env.REACT_APP_API_URL}todo`)
      .then((res) => {
        console.log("Lista de tareas actualizada:", res.data);
        setTodos(res.data);
      })
      .catch((err) => {
        console.error("Error al cargar tareas:", err.message);
      });
  };

  useEffect(() => {
    fetchTodos(); // Cargar las tareas al inicio
  }, []); // Solo cargar una vez al inicio

  // Función para agregar una nueva tarea
  const handleAddTodo = (newTodo) => {
    axios
      .post(`${process.env.REACT_APP_API_URL}todo`, newTodo)
      .then((res) => {
        console.log("Nueva tarea agregada:", res.data);
        fetchTodos(); // Recargar las tareas después de agregar una nueva
      })
      .catch((err) => {
        console.error("Error al crear la tarea:", err.message);
      });
  };

  // Función para actualizar una tarea
  const handleEditTodo = (id, updatedTodo) => {
    axios
      .put(`${process.env.REACT_APP_API_URL}todo/${id}`, updatedTodo)
      .then((res) => {
        console.log("Tarea actualizada:", res.data);
        fetchTodos(); // Recargar las tareas después de actualizar una tarea
      })
      .catch((err) => {
        console.error("Error al actualizar la tarea:", err.message);
      });
  };

  return (
    <Router>
      <nav>
        <ul>
          <li><Link to="/">Crear Tarea</Link></li>
          <li><Link to="/show">Ver Lista de Tareas</Link></li>
        </ul>
      </nav>

      <Routes>
        <Route
          path="/"
          element={<CreateTodo handleAddTodo={handleAddTodo} />}
        />
        <Route
          path="/show"
          element={<ShowTodoList todos={todos} />}
        />
        <Route
          path="/update/:id"
          element={<UpdateTodo todos={todos} handleEditTodo={handleEditTodo} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
