import { useParams, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';

const UpdateTodo = ({ fetchTodos }) => {
  const { id } = useParams();  // Obtener el ID desde la URL
  const navigate = useNavigate();
  const [todo, setTodo] = useState({
    title: '',
    description: '',
    date: '',
    priority: 'Baja', // Valor por defecto
    done: false,
  });

  // Cargar la tarea al montar el componente
  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/api/todos/${id}`)
      .then(res => {
        const todoData = {
          ...res.data,
          date: res.data.date.split('T')[0],
        };
        setTodo(todoData);
      })
      .catch(err => {
        console.error("Error al cargar la tarea:", err.message);
      });
  }, [id]);

  // Manejar cambios en los campos del formulario
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setTodo(prevState => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  // Manejar el envío del formulario
  const handleSubmit = (e) => {
    e.preventDefault();

    axios.put(`${process.env.REACT_APP_API_URL}/api/todos/${id}`, todo)
      .then(res => {
        console.log("Tarea actualizada:", res.data);
        fetchTodos();  // Actualizar la lista de tareas
        navigate('/show');  // Redirigir a la página de ver tareas
      })
      .catch(err => {
        console.error("Error al actualizar la tarea:", err.message);
      });
  };

  return (
    <div className="app-container">
      <h2>Actualizar Tarea</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Título:</label>
          <input
            type="text"
            name="title"
            value={todo.title}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Descripción:</label>
          <textarea
            name="description"
            value={todo.description}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Fecha:</label>
          <input
            type="date"
            name="date"
            value={todo.date}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Prioridad:</label>
          <select
            name="priority"
            value={todo.priority}
            onChange={handleChange}
            required
          >
            <option value="Baja">Baja</option>
            <option value="Media">Media</option>
            <option value="Alta">Alta</option>
          </select>
        </div>
        <div>
          <label>Completada:</label>
          <input
            type="checkbox"
            name="done"
            checked={todo.done}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Actualizar Tarea</button>
      </form>
    </div>
  );
};

export default UpdateTodo;
