import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import ShowTodoList from './components/showTodoList';
import { CreateTodo } from './components/createTodo';
import UpdateTodo from './components/updateTodo';
import './App.scss';
import { useState, useEffect } from 'react';

function App() {
  const [todos, setTodos] = useState([]);

  // Función para obtener las tareas desde la API
  const fetchTodos = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/todo');
      const data = await response.json();
      console.log('Tareas recibidas:', data);
      setTodos(data);
    } catch (error) {
      console.error('Error al obtener tareas:', error);
    }
  };

  // Función para manejar la adición de tareas
  const handleAddTodo = (newTodo) => {
    setTodos((prevTodos) => [...prevTodos, newTodo]); // Agrega la nueva tarea al estado
  };

  // Llamar a la función fetchTodos cuando el componente se monta
  useEffect(() => {
    fetchTodos();
  }, []);

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={<ShowTodoList todos={todos} />}
        />
        <Route
          path="/create"
          element={<CreateTodo handleAddTodo={handleAddTodo} />}
        />
        <Route path="/update/:id" element={<UpdateTodo />} />
      </Routes>
    </Router>
  );
}

export default App;
