import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ShowTodoList = () => {
    // Estado para almacenar las tareas
    const [todos, setTodos] = useState([]);
    const [error, setError] = useState(null);

    // URL del backend
    const API_URL = 'http://192.168.3.188:8000/api/todo';

    // Función para obtener las tareas
    const fetchTodos = async () => {
        try {
            const response = await axios.get(API_URL); // Llama al backend
            setTodos(response.data); // Actualiza el estado con los datos obtenidos
        } catch (err) {
            console.error('Error al obtener TODOs:', err);
            setError('No se pudieron cargar las tareas. Por favor, intenta nuevamente.');
        }
    };

    // Función para manejar la actualización cuando se agregue una nueva tarea
    const handleAddTodo = (newTodo) => {
        setTodos((prevTodos) => [...prevTodos, newTodo]);
    };

    // useEffect para cargar las tareas al montar el componente
    useEffect(() => {
        fetchTodos();
    }, []);

    return (
        <div>
            <h1>Lista de Tareas</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {todos.length > 0 ? (
                <ul>
                    {todos.map((todo) => (
                        <li key={todo._id}>
                            <h2>{todo.title}</h2>
                            <p>{todo.description}</p>
                            <p>Prioridad: {todo.priority}</p>
                            <p>{todo.done ? 'Completado' : 'Pendiente'}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No hay tareas disponibles.</p>
            )}
        </div>
    );
};

export default ShowTodoList;
