import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import ShowTodoList from './components/showTodoList'; 
import CreateTodo from './components/createTodo'; 
import UpdateTodo from './components/updateTodo'; 
import './App.scss'; 

function App() {
  const [todos, setTodos] = useState([]);

  // Cargar todas las tareas al cargar la aplicación
  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}todo`)
      .then((res) => {
        setTodos(res.data);
      })
      .catch((err) => {
        console.error('Error al cargar tareas:', err.message);
      });
  }, []); 

  // Maneja la adición de una nueva tarea
  const handleAddTodo = (newTodo) => {
    axios
      .post(`${process.env.REACT_APP_API_URL}todo`, newTodo)
      .then((res) => {
        console.log('Respuesta del servidor:', res.data); // Muestra la respuesta completa
        if (res.data && res.data._id) {
          setTodos((prevTodos) => [...prevTodos, res.data]); // Asegúrate de agregar el objeto completo de la tarea
        } else {
          console.error('Respuesta inesperada del servidor');
        }
      })
      .catch((err) => {
        console.error("Error al crear la tarea:", err.message);
      });
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={<CreateTodo handleAddTodo={handleAddTodo} />}
        />
        <Route
          path="/show"
          element={<ShowTodoList todos={todos} />}
        />
        <Route
          path="/update/:id"
          element={<UpdateTodo />}
        />
      </Routes>
    </Router>
  );
}

export default App;
