import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

export function CreateTodo({ handleAddTodo }) {
    const [data, setData] = useState({
        title: "",
        description: "",
        date: "",
        priority: "Low",
        done: false,
    });

    const navigate = useNavigate();

    function handleChange(e) {
        const { name, value, type, checked } = e.target;
        setData((prevData) => ({
            ...prevData,
            [name]: type === "checkbox" ? checked : value,
        }));
    }

    function handleSubmit(e) {
        e.preventDefault();

        // Validar campos requeridos
        if (!data.title || !data.description || !data.date) {
            console.error("Todos los campos requeridos deben ser completados.");
            return;
        }

        const todo = {
            title: data.title,
            description: data.description,
            date: data.date,
            priority: data.priority,
            done: data.done,
        };

        axios
            .post(`${process.env.REACT_APP_API_URL}todo`, todo)
            .then((res) => {
                setData({
                    title: "",
                    description: "",
                    date: "",
                    priority: "Low",
                    done: false,
                }); // Resetear el formulario
                console.log("Respuesta del servidor:", res.data);

                // Verifica si el servidor devuelve la tarea completa
                if (res.data && res.data.title) {
                    handleAddTodo(res.data); // Pasa la tarea completa
                } else {
                    console.error("El servidor no devolvió los datos de la tarea correctamente.");
                }

                // Redirigir a la página de lista de tareas después de agregar la tarea
                navigate("/todos");
            })
            .catch((err) => {
                console.error("No se pudo crear la tarea.");
                console.error(err.message);
            });
    }

    return (
        <section className="container">
            <Link to="/" className="button-back">
                <button type="button" className="button">Volver</button>
            </Link>
            <section className="contents">
                <form onSubmit={handleSubmit} className="form-container" noValidate>
                    <label className="label" htmlFor="title">Título</label>
                    <input
                        type="text"
                        name="title"
                        value={data.title}
                        onChange={handleChange}
                        className="input"
                    />
                    <label className="label" htmlFor="description">Descripción</label>
                    <input
                        type="text"
                        name="description"
                        value={data.description}
                        onChange={handleChange}
                        className="input"
                    />
                    <label className="label" htmlFor="date">Fecha</label>
                    <input
                        type="date"
                        name="date"
                        value={data.date}
                        onChange={handleChange}
                        className="input"
                    />
                    <label className="label" htmlFor="priority">Prioridad</label>
                    <select
                        name="priority"
                        value={data.priority}
                        onChange={handleChange}
                        className="input"
                    >
                        <option value="Low">Baja</option>
                        <option value="Medium">Media</option>
                        <option value="High">Alta</option>
                    </select>
                    <label className="label" htmlFor="done">Hecho</label>
                    <input
                        type="checkbox"
                        name="done"
                        checked={data.done}
                        onChange={handleChange}
                        className="checkbox"
                    />
                    <button type="submit" className="button">Crear Tarea</button>
                </form>
            </section>
        </section>
    );
}
