import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

export function UpdateTodo({ handleEditTodo, todos }) {
  const { id } = useParams(); // Obtener el id de la tarea desde la URL
  const navigate = useNavigate(); // Para redirigir después de la actualización
  const [data, setData] = useState({
    title: "",
    description: "",
    date: "",
    priority: "Low",
    done: false,
  });

  useEffect(() => {
    // Cargar datos existentes de la tarea
    const todo = todos.find((todo) => todo._id === id); // Buscar la tarea por id
    if (todo) {
      setData(todo);
    }
  }, [id, todos]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setData((prevData) => ({
      ...prevData,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleEditTodo(id, data); // Llamar la función para actualizar la tarea
    navigate("/show"); // Redirigir a la lista de tareas
  };

  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <label htmlFor="title" className="label">Título</label>
      <input
        type="text"
        name="title"
        className="input"
        value={data.title}
        onChange={handleChange}
      />
      <label htmlFor="description" className="label">Descripción</label>
      <input
        type="text"
        name="description"
        className="input"
        value={data.description}
        onChange={handleChange}
      />
      <label htmlFor="date" className="label">Fecha</label>
      <input
        type="date"
        name="date"
        className="input"
        value={data.date}
        onChange={handleChange}
      />
      <label htmlFor="priority" className="label">Prioridad</label>
      <select
        name="priority"
        className="input"
        value={data.priority}
        onChange={handleChange}
      >
        <option value="Low">Baja</option>
        <option value="Medium">Media</option>
        <option value="High">Alta</option>
      </select>
      <label htmlFor="done" className="label">Hecho</label>
      <input
        type="checkbox"
        name="done"
        className="checkbox"
        checked={data.done}
        onChange={handleChange}
      />
      <button type="submit" className="button">Actualizar</button>
    </form>
  );
}

export default UpdateTodo;
