import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ShowTodoList = () => {
    const [todos, setTodos] = useState([]);
    const [error, setError] = useState(null);

    const fetchTodos = async () => {
        try {
            const response = await axios.get('http://192.168.3.188:8000/api/todo');
            console.log("Tareas obtenidas:", response.data);  // Verifica que la respuesta tenga datos
            setTodos(response.data);
        } catch (err) {
            console.error('Error al obtener TODOs:', err);
            setError('No se pudieron cargar las tareas. Por favor, intenta nuevamente.');
        }
    };

    useEffect(() => {
        console.log("Cargando tareas...");
        fetchTodos();
    }, []);

    return (
        <div>
            <h1>Lista de Tareas</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {todos.length > 0 ? (
                <ul>
                    {todos.map((todo) => (
                        <li key={todo._id}>
                            <h2>{todo.title}</h2>
                            <p>{todo.description}</p>
                            <p>Prioridad: {todo.priority}</p>
                            <p>{todo.done ? 'Completado' : 'Pendiente'}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No hay tareas disponibles.</p>
            )}
        </div>
    );
};

export default ShowTodoList;
