import React, { useState } from 'react';

export const CreateTodo = ({ handleAddTodo }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [priority, setPriority] = useState('Low');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newTodo = {
      title,
      description,
      date,
      priority,
      done: false,
    };

    try {
      const response = await fetch('http://localhost:8000/api/todo', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTodo),
      });

      const data = await response.json();
      console.log('Respuesta del servidor:', data);

      if (response.ok) {
        console.log('Nuevo TODO creado:', data);
        handleAddTodo(data);  // Pasa la tarea recién creada al componente padre
        setTitle('');
        setDescription('');
        setDate('');
        setPriority('Low');
      } else {
        console.error('Error al crear el TODO');
      }
    } catch (error) {
      console.error('Error al crear TODO:', error);
    }
  };

  return (
    <div className="container">
      <h2>To Do App</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Título:</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Descripción:</label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Fecha:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Prioridad:</label>
          <select
            value={priority}
            onChange={(e) => setPriority(e.target.value)}
            required
          >
            <option value="Low">Baja</option>
            <option value="Medium">Media</option>
            <option value="High">Alta</option>
          </select>
        </div>
        <button type="submit">Crear Tarea</button>
      </form>
    </div>
  );
};
